pragma solidity ^0.4.16;

contract ValidExchangeShop {

    struct Remittance {
        uint amount;
        uint deadline;
        address to;
        address owner;
        bool isExists;        
    }

    address public owner;
    uint    public deadlineLimit;
    mapping(bytes32 => uint) public currencies;
    mapping(bytes32 => Remittance) public lockedRemittances;
    mapping(address => uint) public unlockedRemittances;
    uint    internal commissionsAmount;

    modifier onlyOwner(){
        require(msg.sender == owner);
        _;
    }

    modifier onlyInDeadline(uint deadline){
        require(deadline > 0 && deadline <= deadlineLimit);
        _;
    }

    modifier onlyAfterDeadline(bytes32 key){
        require(block.number < lockedRemittances[key].deadline);
        _;
    }

    modifier onlyExistentKey(bytes32 pass1, bytes32 pass2){
        require(lockedRemittances[keccak256(pass1, pass2)].isExists);
        _;
    }

    modifier onlyInExistentKey(bytes32 key){
        require(!lockedRemittances[key].isExists && key.length > 0);
        _;
    }

    modifier onlyValidTo(address to){
         require(to != address(0));
        _;
    }

    modifier onlyInExistentCurrency(bytes32 name){
        require(currencies[name] == uint(0) && name.length > 0);
        _;
    }

    modifier onlyExistentCurrency(bytes32 name){
        require(currencies[name] != uint(0));
        _;
    }

    modifier onlyRemittanceOwner(bytes32 key){
        require(lockedRemittances[key].owner == msg.sender);
        _;
    }
}
